vertexshader.source:
      
      uniform mat4 u_modelViewProjectionMatrix;
      uniform mat4 u_modelViewMatrix;
      uniform mat3 u_normalMatrix;
      uniform vec4 u_lightPosition;
      
      attribute vec4 a_position;
      attribute vec3 a_normal;

      varying vec3 normal;
      varying vec3 lightDir;
      varying vec3 viewDir;

      void main(){
        normal = u_normalMatrix * a_normal;
        vec4 modelCameraPosition = u_modelViewMatrix * a_position;
  
        lightDir = vec3(u_lightPosition - modelCameraPosition);

        viewDir = vec3(-modelCameraPosition);
        gl_Position = u_modelViewProjectionMatrix * a_position;
      }

fragmentshader.source:
      
      uniform int u_lightType;

      uniform vec4 u_materialBrightColor;
      uniform vec4 u_materialDarkColor;
      uniform float u_materialColorSteps;

      varying vec3 normal;
      varying vec3 lightDir;
      varying vec3 viewDir;

      void main(){

        vec3 Ln = normalize(lightDir);
        vec3 Vn = normalize(viewDir);
        vec3 Nn = normalize(normal);

        float intensity = dot(Ln,Nn);
        intensity = floor(intensity * u_materialColorSteps + 0.5) / u_materialColorSteps;
        
        gl_FragData[0].rgb = (u_materialBrightColor * intensity + u_materialDarkColor * (1.0 - intensity)).rgb;
        gl_FragData[1].xyz = Nn;
      }
