  // Only loops 5 times
  for (Integer i=0; ; ++i) {
    if (i == 5)
      break;
  }
}


operator entry() {
  // Only prints 7
  for (Integer i=0; i<10; ++i) {
    if (i != 7)
      continue;
    report(i);
  }
}

